import React, { useEffect, useRef } from "react";
import { gsap } from "gsap";

const Hero = () => {
  const headingRef = useRef(null);

  useEffect(() => {
    gsap.from(headingRef.current, {
      opacity: 0,
      y: 20,
      duration: 1,
      ease: "power2.out",
    });
  }, []);

  return (
    <section className="relative bg-[#F8F6F1] px-6 md:px-12 py-24 text-[#1E1E1E] overflow-hidden">
      {/* Layout row */}
      <div className="relative z-10 flex flex-col md:flex-row items-center justify-between gap-16 md:gap-24">

        {/* Left Text */}
        <div className="max-w-md space-y-6 md:mt-20">
          <p className="text-base leading-6">
            Transform your skincare routine with premium products that restore, protect, and enhance your natural glow every day.
          </p>
          <button className="bg-[#1E1E1E] text-white px-6 py-2 rounded-full text-sm hover:bg-[#333] transition">
            Shop Now
          </button>
        </div>

        {/* Right Image & Text Block */}
        <div className="flex flex-col items-center text-center space-y-6">
          {/* GLOW NATURALLY */}
          <h2
            ref={headingRef}
            className="text-4xl md:text-6xl font-bold text-[#2D3B36] leading-tight"
          >
            GLOW<br />NATURALLY
          </h2>

          {/* Hero Image */}
          <div className="relative">
            <img
              src="/assets/hero.jpg"
              alt="Glow Skincare"
              className="w-[300px] md:w-[420px] rounded-[20px] object-cover"
            />
            {/* Overlay Label */}
            <div className="absolute bottom-4 left-4 bg-white shadow-md backdrop-blur-md px-4 py-2 rounded-full flex items-center text-sm gap-2 max-w-[90%]">
              <span className="text-lg">ðŸ§´</span>
              <span className="text-[#1E1E1E]">
                While giving you an invigorating cleansing experience.
              </span>
            </div>
          </div>
        </div>
      </div>

      {/* SKINCARE Background Word */}
      <h1 className="absolute -bottom-2 left-6 md:left-12 text-[18vw] md:text-[12vw] font-extrabold text-[#2D3B36]/10 leading-none z-0 pointer-events-none">
        SKINCARE
      </h1>

      {/* Full Paragraph Below */}
      <div className="relative z-10 mt-24 text-center md:max-w-5xl mx-auto text-base md:text-lg leading-relaxed px-4">
        <p>
          <strong>Experience the ultimate in skincare</strong> with our expertly formulated products, crafted to nourish, protect, and rejuvenate your skin. Combining the finest natural ingredients with <span className="text-[#888]">advanced science</span>, our collection ensures every skin type can achieve a radiant, healthy glow. Embrace your beauty with confidence every day.
        </p>
      </div>
    </section>
  );
};

export default Hero;
