import React, { useEffect, useRef } from "react";
import { gsap } from "gsap";

const Hero = () => {
  const textRef = useRef(null);

  useEffect(() => {
    gsap.from(textRef.current, {
      opacity: 0,
      y: 30,
      duration: 1,
      ease: "power2.out",
    });
  }, []);

  return (
    <section className="relative bg-[#F8F6F1] px-6 md:px-12 py-24 text-[#1E1E1E] overflow-hidden">
      {/* Layout row */}
      <div className="relative z-10 flex flex-col md:flex-row items-center justify-between gap-12">
        {/* Left: Text and CTA */}
        <div className="max-w-md space-y-6">
          <p className="text-base leading-6">
            Transform your skincare routine with premium products that restore, protect, and enhance your natural glow every day.
          </p>
          <button className="bg-[#1E1E1E] text-white px-6 py-2 rounded-full text-sm hover:bg-[#333] transition">
            Shop Now
          </button>
        </div>

        {/* Right: Image + Label */}
        <div className="relative">
          <img
            src="/assets/hero.jpg"
            alt="Skincare Hero"
            className="w-[300px] md:w-[420px] rounded-[20px] object-cover"
          />
          <div className="absolute bottom-4 left-4 bg-white shadow-md backdrop-blur-md px-4 py-2 rounded-full flex items-center text-sm gap-2 max-w-[90%]">
            <span className="text-lg">ðŸ§´</span>
            <span className="text-[#1E1E1E]">While giving you an invigorating cleansing experience.</span>
          </div>
        </div>
      </div>

      {/* SKINCARE background heading */}
      <h1 className="absolute bottom-4 left-6 md:left-12 text-[18vw] md:text-[12vw] font-extrabold text-[#4B5E3D]/10 leading-none z-0 pointer-events-none">
        SKINCARE
      </h1>

      {/* Bottom paragraph */}
      <div
        ref={textRef}
        className="relative z-10 mt-24 text-center md:max-w-5xl mx-auto text-base md:text-lg leading-relaxed px-4"
      >
        <p>
          <strong>Experience the ultimate in skincare</strong> with our expertly formulated products, crafted to nourish, protect, and rejuvenate your skin. Combining the finest natural ingredients with <span className="text-[#888]">advanced science</span>, our collection ensures every skin type can achieve a radiant, healthy glow. Embrace your beauty with confidence every day.
        </p>
      </div>
    </section>
  );
};

export default Hero;
