import React, { useEffect, useRef } from "react";
import { gsap } from "gsap";

const Hero = () => {
  const headingRef = useRef(null);

  useEffect(() => {
    gsap.from(headingRef.current, {
      opacity: 0,
      y: 20,
      duration: 1,
      ease: "power2.out",
    });
  }, []);

  return (
    <section className="relative bg-[#F2F1EB] px-6 py-20 text-[#1E1E1E] overflow-hidden">
      {/* SKINCARE background word */}
      <h1 className="absolute bottom-0 left-1 text-[20vw] md:text-[14vw] font-extrabold text-[#2D3B36]/10 z-0 leading-none">
        SKINCARE
      </h1>

      {/* Main Layout */}
      <div className="relative z-10 flex flex-col md:flex-row items-center justify-between gap-10">
        {/* Left Side: Text + Button */}
        <div className="max-w-sm space-y-4 text-center md:text-left">
          <p className="text-sm font-light leading-6 text-[#333]">
            Transform your skincare routine with premium products that restore, protect, and enhance your natural glow every day.
          </p>
          <button className="bg-[#1E1E1E] text-white px-5 py-2 rounded-full text-sm hover:opacity-80 transition">
            Shop Now
          </button>
        </div>

        {/* Right Side: Heading + Image */}
        <div className="flex flex-col items-center text-center space-y-6">
          {/* GLOW NATURALLY Heading */}
          <h2
            ref={headingRef}
            className="text-4xl md:text-6xl font-bold text-[#2D3B36] leading-tight"
          >
            GLOW<br />NATURALLY
          </h2>

          {/* Image with Label */}
          <div className="relative">
            <img
              src="/assets/hero.jpg"
              alt="Glow Skincare"
              className="w-[300px] md:w-[420px] rounded-[20px] object-cover"
            />
            <div className="absolute bottom-4 left-4 bg-white/90 backdrop-blur-md px-4 py-2 rounded-full flex items-center text-sm gap-2 max-w-[90%] shadow-md">
              <span className="text-lg">ðŸ§´</span>
              <span className="text-[#1E1E1E]">While giving you an invigorating cleansing experience.</span>
            </div>
          </div>
        </div>
      </div>

      {/* Bottom Paragraph */}
      <div className="mt-16 text-center md:max-w-4xl mx-auto text-base md:text-lg leading-relaxed text-[#1E1E1E] px-4">
        <p>
          <strong>Experience the ultimate in skincare</strong> with our expertly formulated products, crafted to nourish, protect, and rejuvenate your skin. Combining the finest natural ingredients with <span className="text-[#A1A1A1]">advanced science</span>, our collection ensures every skin type can achieve a radiant, healthy glow. Embrace your beauty with confidence every day.
        </p>
      </div>
    </section>
  );
};

export default Hero;
