import React, { useEffect, useRef } from "react";
import { gsap } from "gsap";

const Hero = () => {
  const titleRef = useRef();

  useEffect(() => {
    gsap.fromTo(titleRef.current, { opacity: 0, y: 50 }, { opacity: 1, y: 0, duration: 1 });
  }, []);

  return (
    <section className="flex flex-col md:flex-row items-center justify-between px-6 py-20 fade-in">
      <div className="md:w-1/2 space-y-4">
        <h2 ref={titleRef} className="text-4xl md:text-6xl font-bold">
          Glow Naturally
        </h2>
        <p className="text-lg">
          Discover skincare that makes you feel radiant inside and out.
        </p>
        <button className="bg-green text-white px-6 py-2 rounded-full hover:bg-opacity-80 transition">
          Shop Now
        </button>
      </div>
      <div className="md:w-1/2 mt-10 md:mt-0">
        <img src="/assets/hero.jpg" alt="Skincare Hero" className="w-full rounded-lg" />
      </div>
    </section>
  );
};

export default Hero;
