import React, { useState } from "react";

const faqs = [
  {
    question: "Are your products cruelty-free?",
    answer: "Yes, all of our products are 100% cruelty-free and ethically sourced.",
  },
  {
    question: "What skin types are your products suitable for?",
    answer: "Our products are formulated for all skin types, including sensitive skin.",
  },
  {
    question: "Do you ship internationally?",
    answer: "Yes, we ship to over 50 countries worldwide with fast, tracked delivery.",
  },
];

const FAQ = () => {
  const [openIndex, setOpenIndex] = useState(null);

  const toggle = (index) => {
    setOpenIndex(openIndex === index ? null : index);
  };

  return (
    <section className="px-6 py-16">
      <h3 className="text-2xl font-semibold text-center mb-6">Answers to Your Skincare Questions</h3>
      <div className="max-w-2xl mx-auto space-y-4">
        {faqs.map((item, index) => (
          <div key={index} className="border-b pb-4">
            <button
              className="w-full text-left font-medium text-lg flex justify-between items-center"
              onClick={() => toggle(index)}
            >
              {item.question}
              <span>{openIndex === index ? "−" : "+"}</span>
            </button>
            <div
              className={`transition-all duration-300 ease-in-out overflow-hidden ${
                openIndex === index ? "max-h-40 opacity-100 mt-2" : "max-h-0 opacity-0"
              }`}
            >
              <p className="text-gray-700">{item.answer}</p>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
};

export default FAQ;
