import React, { useRef } from "react";
import { FaArrowLeft, FaArrowRight, FaShoppingCart } from "react-icons/fa";

const products = [
  {
    name: "ALYA SKIN CLEANSER",
    price: "FROM $29.99",
    image: "/assets/product-1.jpg",
  },
  {
    name: "RITUAL OF SAKURA",
    price: "FROM $22.00",
    image: "/assets/product-2.jpg",
  },
  {
    name: "THE BODY LOTION",
    price: "FROM $19.00",
    image: "/assets/product-3.jpg",
  },
];

const ProductCarousel = () => {
  const sliderRef = useRef();

  const scroll = (dir) => {
    if (sliderRef.current) {
      const amount = dir === "left" ? -300 : 300;
      sliderRef.current.scrollBy({ left: amount, behavior: "smooth" });
    }
  };

  return (
    <section className="bg-[#F2F1EB] px-6 py-16 text-[#1E1E1E]">
      <div className="max-w-6xl mx-auto space-y-8">
        {/* Header */}
        <div className="flex flex-col items-center justify-center space-y-2">
          <button className="border border-[#1E1E1E] px-4 py-1 rounded-full text-sm hover:bg-[#1E1E1E] hover:text-white transition">
            Best Selling Products
          </button>
          <h2 className="text-xl md:text-2xl font-semibold text-center">
            Skincare That Brings Out <br className="hidden md:block" />
            Your Natural Radiance
          </h2>
        </div>

        {/* Carousel with buttons */}
        <div className="relative">
          {/* Scroll Buttons */}
          <button
            onClick={() => scroll("left")}
            className="absolute left-[-30px] top-1/2 transform -translate-y-1/2 bg-[#1E1E1E] text-white p-2 rounded-full z-10 hover:bg-[#333] hidden md:block"
          >
            <FaArrowLeft size={14} />
          </button>

          <div
            ref={sliderRef}
            className="flex overflow-x-auto gap-6 scroll-smooth no-scrollbar"
          >
            {products.map((item, index) => (
              <div
                key={index}
                className={`min-w-[260px] max-w-[260px] bg-white rounded-xl shadow-md overflow-hidden border-2 ${
                  index === 1 ? "border-[#0E6FFF]" : "border-transparent"
                }`}
              >
                <img
                  src={item.image}
                  alt={item.name}
                  className="w-full h-60 object-cover"
                />
                <div className="flex justify-between items-center p-4">
                  <div>
                    <h3 className="font-semibold text-sm">{item.name}</h3>
                    <p className="text-xs text-gray-500">{item.price}</p>
                  </div>
                  <button className="bg-[#F2F1EB] p-2 rounded-full hover:scale-105 transition">
                    <FaShoppingCart size={14} />
                  </button>
                </div>
              </div>
            ))}
          </div>

          <button
            onClick={() => scroll("right")}
            className="absolute right-[-30px] top-1/2 transform -translate-y-1/2 bg-[#1E1E1E] text-white p-2 rounded-full z-10 hover:bg-[#333] hidden md:block"
          >
            <FaArrowRight size={14} />
          </button>
        </div>
      </div>
    </section>
  );
};

export default ProductCarousel;
