import React, { useRef } from "react";
import { gsap } from "gsap";

const products = [
  { name: "Radiant Serum", price: "$29", image: "/assets/product1.jpg" },
  { name: "Glow Moisturizer", price: "$39", image: "/assets/product2.jpg" },
  { name: "Herbal Cleanser", price: "$19", image: "/assets/product3.jpg" },
  { name: "Soothing Toner", price: "$25", image: "/assets/product4.jpg" }
];

const ProductCarousel = () => {
  const sliderRef = useRef();

  const slide = (direction) => {
    const container = sliderRef.current;
    if (container) {
      const scrollAmount = direction === "left" ? -300 : 300;
      gsap.to(container, { scrollLeft: container.scrollLeft + scrollAmount, duration: 0.5 });
    }
  };

  return (
    <section className="px-6 py-12 text-center">
      <h3 className="text-2xl font-semibold mb-6">Best Selling Products</h3>
      <div className="relative">
        <button
          className="absolute left-0 top-1/2 transform -translate-y-1/2 bg-green text-white px-3 py-1 rounded-full z-10"
          onClick={() => slide("left")}
        >
          ←
        </button>
        <div
          ref={sliderRef}
          className="overflow-x-auto flex space-x-6 scroll-smooth pb-4"
        >
          {products.map((product, index) => (
            <div
              key={index}
              className="min-w-[250px] bg-white rounded-lg shadow-lg p-4 transform transition hover:scale-105"
            >
              <img
                src={product.image}
                alt={product.name}
                className="w-full h-40 object-cover rounded-md mb-3"
              />
              <h4 className="font-medium">{product.name}</h4>
              <p className="text-green font-semibold">{product.price}</p>
            </div>
          ))}
        </div>
        <button
          className="absolute right-0 top-1/2 transform -translate-y-1/2 bg-green text-white px-3 py-1 rounded-full z-10"
          onClick={() => slide("right")}
        >
          →
        </button>
      </div>
    </section>
  );
};

export default ProductCarousel;
